import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const location = useLocation();

  const navItems = [
    { name: 'AcasƒÉ', path: '/' },
    { name: 'Despre noi', path: '/despre-noi' },
    { name: 'Servicii', path: '/servicii' },
    { name: 'Galerie', path: '/galerie' },
    { name: 'Contact', path: '/contact' },
  ];

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <nav className="navbar">
      <div className="nav-container">
        <Link to="/" className="nav-logo">
          <span className="logo-icon">üè°</span>
          Ringabell
          <span className="logo-sparkle">‚ú®</span>
        </Link>
        
        <ul className={`nav-menu ${isMenuOpen ? 'active' : ''}`}>
          {navItems.map((item) => (
            <li key={item.path}>
              <Link
                to={item.path}
                className={`nav-link ${location.pathname === item.path ? 'active' : ''}`}
                onClick={() => setIsMenuOpen(false)}
              >
                <span className="nav-link-icon">
                  {item.name === 'AcasƒÉ' && 'üè†'}
                  {item.name === 'Despre noi' && 'üë•'}
                  {item.name === 'Servicii' && 'üéØ'}
                  {item.name === 'Galerie' && 'üì∏'}
                  {item.name === 'Contact' && 'üìû'}
                </span>
                {item.name}
              </Link>
            </li>
          ))}
        </ul>

        {/* Friendly Mobile Menu */}
        <div className="mobile-menu" onClick={toggleMenu}>
          <span className="hamburger-line"></span>
          <span className="hamburger-line"></span>
          <span className="hamburger-line"></span>
        </div>
      </div>
      
      {/* Decorative elements for friendly touch */}
      <div className="nav-decorations">
        <div className="nav-bubble bubble-1"></div>
        <div className="nav-bubble bubble-2"></div>
        <div className="nav-bubble bubble-3"></div>
      </div>
    </nav>
  );
};

export default Navbar;
