import React, { useEffect, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const location = useLocation();

  const navItems = [
    { name: 'Acasă', path: '/' },
    { name: 'Despre noi', path: '/despre-noi' },
    { name: 'Servicii', path: '/servicii' },
    { name: 'Galerie', path: '/galerie' },
    { name: 'Contact', path: '/contact' },
  ];

  const toggleMenu = () => {
    setIsMenuOpen((prev) => !prev);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  // Lock body scroll when the colorful mobile drawer is open
  useEffect(() => {
    if (isMenuOpen) {
      document.body.classList.add('nav-open');
    } else {
      document.body.classList.remove('nav-open');
    }

    return () => {
      document.body.classList.remove('nav-open');
    };
  }, [isMenuOpen]);

  return (
    <nav className="navbar">
      <div className="nav-container">
        <Link
          to="/"
          className="nav-logo"
        >
          Ringabell
        </Link>

        {/* Desktop menu + colorful mobile drawer content */}
        <div className={`nav-menu ${isMenuOpen ? 'open' : ''}`} id="primary-navigation">
          <div className="nav-links">
            {navItems.map((item) => (
              <Link
                key={item.path}
                to={item.path}
                className={`nav-link ${
                  location.pathname === item.path ? 'active' : ''
                }${item.path === '/contact' ? ' nav-link--cta' : ''}`}
                onClick={closeMenu}
              >
                {item.name}
              </Link>
            ))}
          </div>
        </div>

        {/* Mobile hamburger button */}
        <button
          className="mobile-menu-btn"
          onClick={toggleMenu}
          aria-label="Deschide meniul"
          aria-expanded={isMenuOpen}
          aria-controls="primary-navigation"
        >
          <svg width="26" height="20" viewBox="0 0 26 20" fill="none" xmlns="http://www.w3.org/2000/svg">
            <rect x="2" y="2" width="22" height="3" rx="1.5" fill="#2563eb"/>
            <rect x="2" y="8.5" width="22" height="3" rx="1.5" fill="#2563eb"/>
            <rect x="2" y="15" width="22" height="3" rx="1.5" fill="#2563eb"/>
          </svg>
        </button>
      </div>

      {/* Decorative floating bubbles to keep it kid-friendly */}
      <div className="nav-decorations">
        <div className="nav-bubble bubble-1"></div>
        <div className="nav-bubble bubble-2"></div>
        <div className="nav-bubble bubble-3"></div>
      </div>
    </nav>
  );
};

export default Navbar;
