import React, { useState, useEffect } from 'react';
import { Link, NavLink, useLocation } from 'react-router-dom';

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const location = useLocation();

  const closeMenu = () => setIsMenuOpen(false);
  const toggleMenu = () => setIsMenuOpen(prev => !prev);

  useEffect(() => {
    // close menu when route changes
    setIsMenuOpen(false);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  }, [location.pathname]);

  useEffect(() => {
    if (isMenuOpen) {
      document.body.classList.add('nav-open');
    } else {
      document.body.classList.remove('nav-open');
    }
  }, [isMenuOpen]);

  const navLinkClass = ({ isActive }) =>
    `nav-link${isActive ? ' nav-link--active' : ''}`;

  return (
    <>
      <nav className={`navbar ${isMenuOpen ? 'navbar--open' : ''}`}>
        <div className="nav-container">
          {/* LOGO */}
          <Link to="/" className="nav-logo" onClick={closeMenu}>
            Ringabell
          </Link>

          {/* DESKTOP LINKS */}
          <div className="nav-links">
            <NavLink to="/" end className={navLinkClass}>
              Acasă
            </NavLink>
            <NavLink to="/despre-noi" className={navLinkClass}>
              Despre noi
            </NavLink>
            <NavLink to="/servicii" className={navLinkClass}>
              Servicii
            </NavLink>
            <NavLink to="/galerie" className={navLinkClass}>
              Galerie
            </NavLink>
            <NavLink to="/contact" className={navLinkClass}>
              Contact
            </NavLink>
          </div>

          {/* MOBILE HAMBURGER */}
          <button
            type="button"
            className={`mobile-menu ${isMenuOpen ? 'active' : ''}`}
            onClick={toggleMenu}
            aria-label="Deschide meniul"
            aria-expanded={isMenuOpen}
          >
            <span className="hamburger-line" />
            <span className="hamburger-line" />
            <span className="hamburger-line" />
          </button>

          {/* SLIDE-IN MOBILE MENU */}
          <div className={`nav-menu ${isMenuOpen ? 'open' : ''}`}>
            <NavLink to="/" end className={navLinkClass} onClick={closeMenu}>
              Acasă
            </NavLink>
            <NavLink
              to="/despre-noi"
              className={navLinkClass}
              onClick={closeMenu}
            >
              Despre noi
            </NavLink>
            <NavLink
              to="/servicii"
              className={navLinkClass}
              onClick={closeMenu}
            >
              Servicii
            </NavLink>
            <NavLink
              to="/galerie"
              className={navLinkClass}
              onClick={closeMenu}
            >
              Galerie
            </NavLink>
            <NavLink
              to="/contact"
              className={navLinkClass}
              onClick={closeMenu}
            >
              Contact
            </NavLink>
          </div>

          {/* DECORATIVE BUBBLES (unchanged) */}
          <div className="nav-bubble bubble-1"></div>
          <div className="nav-bubble bubble-2"></div>
          <div className="nav-bubble bubble-3"></div>
        </div>
      </nav>

      {/* LIGHT BACKDROP – CLICK TO CLOSE */}
      <div
        className={`nav-backdrop ${isMenuOpen ? 'active' : ''}`}
        onClick={closeMenu}
      />
    </>
  );
};

export default Navbar;
