import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Navbar = () => {
  const location = useLocation();

  const navItems = [
    { name: 'Acasă', path: '/' },
    { name: 'Despre noi', path: '/despre-noi' },
    { name: 'Servicii', path: '/servicii' },
    { name: 'Galerie', path: '/galerie' },
    { name: 'Contact', path: '/contact' },
  ];

  return (
    <>
    <nav className={`navbar ${isMenuOpen ? 'navbar--open' : ''}`}>
      <div className="nav-container">
        <Link
          to="/"
          className="nav-logo"
          onClick={closeMenu}
        >
          Ringabell
        </Link>

        {/* Desktop menu + colorful mobile drawer content */}
        <div className={`mobile-menu-panel ${isMenuOpen ? 'open' : ''}`} id="primary-navigation">
          <div className="nav-links">
            {navItems.map((item) => (
              <Link
                key={item.path}
                to={item.path}
                className={`nav-link ${
                  location.pathname === item.path ? 'active' : ''
                }${item.path === '/contact' ? ' nav-link--cta' : ''}`}
                onClick={closeMenu}
              >
                {item.name}
              </Link>
            ))}
          </div>
        </div>
      </div>


      {/* Decorative floating bubbles to keep it kid-friendly */}
      <div className="nav-decorations">
        <div className="nav-bubble bubble-1"></div>
        <div className="nav-bubble bubble-2"></div>
        <div className="nav-bubble bubble-3"></div>
      </div>
    </nav>

    <div
      className={`mobile-menu-overlay ${isMenuOpen ? 'active' : ''}`}
      onClick={closeMenu}
    />
    </>
  );
};

export default Navbar;
